const estado = flow.get("estadoLampada") || "Desconhecido";
const estado1 = flow.get("estadoTensao") || "Desconhecido";
const estado2 = flow.get("estadoCorrente") || "Desconhecido";

const agora = new Date();

// Recupera o histórico anterior ou cria um novo array
let historicoL = flow.get("historicoLampada") || [];
let historicoT = flow.get("historicoTensao") || [];
let historicoC = flow.get("historicoCorrente") || [];


// Adiciona o novo registro
historicoL.push({
    estado: estado,
    dataHora: agora.toISOString()
});

historicoT.push({
    estado: estado1,
    dataHora: agora.toISOString()
});

historicoC.push({
    estado: estado2,
    dataHora: agora.toISOString()
});





// Remove registros com mais de 24 horas
const limite = new Date(agora.getTime() - 24 * 60 * 60 * 1000);
historicoL = historicoL.filter(registro => new Date(registro.dataHora) >= limite);
historicoT = historicoT.filter(registro => new Date(registro.dataHora) >= limite);
historicoC = historicoC.filter(registro => new Date(registro.dataHora) >= limite);

// Salva o histórico atualizado
flow.set("historicoLampada", historicoL);
flow.set("historicoTensao", historicoT);
flow.set("historicoCorrente", historicoC);


// Gera o relatório
let relatorio = "Relatório do Sistema (Últimas 24 horas)\n----------------------\n";
historicoL.forEach(registro => {
    const dataFormatada = new Date(registro.dataHora).toLocaleString();
    relatorio += `Estado da Lâmpada: ${registro.estado} | Data/Hora: ${dataFormatada}\n`;
    relatorio += `Valor da tensão: ${registro.estado1} | Data/Hora: ${dataFormatada}\n`;
    relatorio += `Valor da corrente: ${registro.estado2} | Data/Hora: ${dataFormatada}\n`;

});

msg.payload = relatorio;
return msg;

